info.description:
{
  "openapi": "3.0.0",
  "info": {
    "title": "URL Shortener API",
    "version": "1.0.0"
  },
  "paths": {
    "/shorten": {
      "post": {
        "summary": "Shorten a URL",
        "requestBody": {
          "required": true,
          "content": {
            "application/json": {
              "schema": {
                "$ref": "#/components/schemas/ShortenUrlRequest"
              }
            }
          }
        },
        "responses": {
          "200": {
            "description": "Successful response",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/ShortenUrlResponse"
                }
              }
            }
          }
        }
      }
    },
    "/qrcode": {
      "post": {
        "summary": "Generate QR Code",
        "requestBody": {
          "required": true,
          "content": {
            "application/json": {
              "schema": {
                "$ref": "#/components/schemas/QRCodeRequest"
              }
            }
          }
        },
        "responses": {
          "200": {
            "description": "Successful response",
            "content": {
              "image/png": {
                "schema": {
                  "type": "string",
                  "format": "binary"
                }
              }
            }
          }
        }
      }
    }
  },
  "components": {
    "schemas": {
      "ShortenUrlRequest": {
        "type": "object",
        "properties": {
          "originalUrl": {
            "type": "string"
          },
          "customUrl": {
            "type": "string"
          }
        }
      },
      "ShortenUrlResponse": {
        "type": "object",
        "properties": {
          "originalUrl": {
            "type": "string"
          },
          "shortUrl": {
            "type": "string"
          },
          "customUrl": {
            "type": "string"
          }
        }
      },
      "QRCodeRequest": {
        "type": "object",
        "properties": {
          "url": {
            "type": "string"
          }
        }
      }
    }
  }
}
